import React, {useEffect, useState} from 'react';
import axios from 'axios';


function Countries() {

     const [countryStats, setCountryStats] = useState([{}]);
 
     
   useEffect(() => {
     
 
 
    axios({
      "method":"GET",
      "url":"https://covid-193.p.rapidapi.com/statistics",
      "headers":{
      "content-type":"application/octet-stream",
      "x-rapidapi-host":"covid-193.p.rapidapi.com",
      "x-rapidapi-key":"c0dba4d6a3msh6c39589aa21d903p1b6ce4jsn8b41b9308353"
      }
      })
         .then((response)=>{
           console.log(response.data.response)
            setCountryStats(response.data.response)
         })
         .catch((error)=>{
           console.log(error)
         })
     }, []);
 
   return (
     <>
     <h1>Stats by Country</h1>
     <div className= "section">
       
       {countryStats.map(item => {
           return( 
             <div className= "card" >
             <h2>{item.country}</h2>
{item.map(stuff => {
           return( 
         <div>
           <h3>Total cases:{stuff.deaths.new} </h3>
           <h4>Total deaths</h4>
           <h4><span>num</span></h4>
           <h4>Total recovered</h4>
           <h4 className="life">rec</h4>
           <h4>24hr Deaths:num</h4>
           <h4>New cases: {item.day}</h4>
           
           </div>
           
             )
             
         })}
       
           </div>
           
             )
             
         })}
     </div>
     </>
   );
 }
 
 export default Countries;
